import { useState } from 'react'
import Head from 'next/head'
import { Layout } from 'antd'

const {
  Header,
  Footer,
  Sider,
  Content
} = Layout

const axios = require('axios')

const Index = () => {

  const [inputText, setInputText] = useState('')
  const [searchRes, setSearchRes] = useState([])

  const getMovies = (e) => {
    e.preventDefault()

    axios.get(`http://www.omdbapi.com/?apikey=cda2a2d3&i=tt3896198&s=${inputText}`)
      .then(res => {
        setSearchRes(res.data.Search)
        console.log(res.data)
      })
      .catch(err => console.log(err))

    setInputText('')
  }

  const handleChange = (e) => {
    setInputText(e.target.value)
  }

  return (
    <div>
      <Head>
        <link rel="stylesheet" href="https://bootswatch.com/4/united/bootstrap.min.css" />
      </Head>
      <div>
        <h4>Movie Database Search</h4>
        <form>
          <input type="text" value={inputText} onChange={handleChange} className='mb-3' />
          <br />
          <button onClick={getMovies}>Search Movies</button>
        </form>

        <br />

        {
          searchRes.length >= 1 ?
            searchRes.map((result, idx) => {
              return (
                <div className="card" style={{ width: '18rem' }} key={idx}>
                  <img className="card-img-top" src={result.Poster} alt="Card image cap" />
                  <div className="card-body">
                    <h4>{result.Title}</h4>
                    <p>{result.Type}</p>
                  </div>
                  <div className="card-body">
                    <a href="#" className="card-link">Card link</a>
                    <a href="#" className="card-link">Another link</a>
                  </div>
                </div>
              )
            })
            : 'No movies searched'
        }
      </div>
    </div>
  )
}

export default Index
